def sum_list(numbers):
    total = 0
    for num in numbers:
        total += num
    return total

print(sum_list([1, 2, 3, 4]))

def find_max(numbers):
    max_num = numbers[0]
    for num in numbers:
        if num > max_num:
            max_num = num
    return max_num

print(find_max([3, 8, 2, 9]))

def reverse_list(items):
    for item in reversed(items):
        print(item)

reverse_list(["apple", "banana", "cherry"])
